def minReverseOperations(n: int, p: int, banned: List[int], k: int) -> List[int]:
    banned = set(banned)
    ans = [-1] * n
    ans[p] = 0
    for i in range(p - 1, -1, -1):
        if i + k > p or any(j in banned for j in range(i, i + k)):
            continue
        ans[i] = ans[i + k] + 1 if ans[i + k] != -1 else -1
    for i in range(p + 1, n):
        if i - k < p or any(j in banned for j in range(i - k + 1, i + 1)):
            continue
        ans[i] = ans[i - k] + 1 if ans[i - k] != -1 else -1
    return ans

