def findTheString(lcp: List[List[int]]) -> str:
    n = len(lcp)
    word = ['a'] * n
    graph = [[] for _ in range(n)]
    indegree = [0] * n

    for i in range(n):
        for j in range(i + 1, n):
            if lcp[i][j] < min(len(word[i]), len(word[j])):
                if word[i][lcp[i][j]] < word[j][lcp[i][j]]:
                    graph[i].append(j)
                    indegree[j] += 1
                else:
                    graph[j].append(i)
                    indegree[i] += 1

    queue = [i for i in range(n) if indegree[i] == 0]
    heapq.heapify(queue)

    while queue:
        i = heapq.heappop(queue)
        for j in graph[i]:
            indegree[j] -= 1
            if indegree[j] == 0:
                heapq.heappush(queue, j)
                word[j] = word[i] + chr(ord(word[i][-1]) + 1)

    return ''.join(word) if all(indegree[i] == 0 for i in range(n)) else ''

