def minimumScore(s: str, t: str) -> int:
    n, m = len(s), len(t)
    left, right = [0]*m, [0]*m
    j = 0
    for i in range(n):
        if s[i] == t[j]:
            left[j] = i
            if j > 0:
                left[j] = min(left[j], left[j-1])
            j += 1
            if j == m:
                break
    j = m - 1
    for i in range(n-1, -1, -1):
        if s[i] == t[j]:
            right[j] = i
            if j < m - 1:
                right[j] = max(right[j], right[j+1])
            j -= 1
            if j == -1:
                break
    ans = min(right[i] - left[i] + 1 for i in range(m-1)) 
    ans = min(ans, right[0] + 1, n - left[m-1])
    return ans

