def minimumVisitedCells(grid: List[List[int]]) -> int:
    m, n = len(grid), len(grid[0])
    heap = [(0, 0, 0)]  # (distance, row, col)
    visited = {(0, 0): 0}

    while heap:
        dist, i, j = heapq.heappop(heap)
        if (i, j) == (m - 1, n - 1):
            return dist + 1
        for ni, nj in [(i, j + k) for k in range(1, grid[i][j] + 1) if j + k < n] + [(i + k, j) for k in range(1, grid[i][j] + 1) if i + k < m]:
            if (ni, nj) not in visited or visited[(ni, nj)] > dist + 1:
                visited[(ni, nj)] = dist + 1
                heapq.heappush(heap, (dist + 1, ni, nj))

    return -1

