def putMarbles(weights: List[int], k: int) -> int:
    n = len(weights)
    dp = [[0]*n for _ in range(k)]
    prefix_sum = [0]*(n+1)
    for i in range(1, n+1):
        prefix_sum[i] = prefix_sum[i-1] + weights[i-1]
    for i in range(n):
        dp[0][i] = prefix_sum[i+1]
    for i in range(1, k):
        for j in range(i, n):
            dp[i][j] = min(dp[i-1][x] + prefix_sum[j+1] - prefix_sum[x+1] for x in range(i-1, j))
    return max(dp[-1]) - min(dp[-1])

