def rootCount(edges: List[List[int]], guesses: List[List[int]], k: int) -> int:
    n = len(edges) + 1
    graph = defaultdict(list)
    for u, v in edges:
        graph[u].append(v)
        graph[v].append(u)

    guess_count = [0] * n
    for u, v in guesses:
        guess_count[u] += 1

    def dfs(node, parent):
        count = guess_count[node]
        for child in graph[node]:
            if child == parent:
                continue
            count += dfs(child, node)
        return count

    possible_roots = 0
    for node in range(n):
        if dfs(node, -1) >= k:
            possible_roots += 1

    return possible_roots

