def waysToReachTarget(target: int, types: List[List[int]]) -> int:
    MOD = 10**9 + 7
    dp = [0] * (target + 1)
    dp[0] = 1
    for count, marks in types:
        for j in range(target, -1, -1):
            for k in range(1, min(count, j // marks) + 1):
                dp[j] = (dp[j] + dp[j - k * marks]) % MOD
    return dp[target]

