def findValidSplit(nums: List[int]) -> int:
    def product(lst):
        return reduce(lambda x, y: x * y, lst)

    for i in range(len(nums) - 1):
        if gcd(product(nums[:i+1]), product(nums[i+1:])) == 1:
            return i
    return -1

