def maxOutput(n: int, edges: List[List[int]], price: List[int]) -> int:
    graph = collections.defaultdict(list)
    for u, v in edges:
        graph[u].append(v)
        graph[v].append(u)

    def dfs(node, parent):
        total[node] = price[node]
        max_path[node] = price[node]
        for child in graph[node]:
            if child == parent:
                continue
            dfs(child, node)
            total[node] += total[child]
            max_path[node] = max(max_path[node], total[child])

    def dfs2(node, parent):
        max_path[node] = max(max_path[node], total[0] - total[node])
        for child in graph[node]:
            if child == parent:
                continue
            total[child] += total[0] - total[child]
            dfs2(child, node)

    total = [0] * n
    max_path = [0] * n
    dfs(0, -1)
    dfs2(0, -1)
    return max(max_path)

