def maxPower(stations: List[int], r: int, k: int) -> int:
    n = len(stations)
    prefix_sum = [0] * (n + 2 * r + 1)
    for i in range(n):
        prefix_sum[i + r + 1] = stations[i]
    for i in range(1, len(prefix_sum)):
        prefix_sum[i] += prefix_sum[i - 1]
    max_power = [0] * (n + 2 * r + 1)
    for i in range(r + 1, n + r + 1):
        max_power[i] = max(max_power[i - 1], prefix_sum[i] - prefix_sum[i - r - 1] + k)
    return max_power[n + r]

