def countPartitions(nums: List[int], k: int) -> int:
    MOD = 10**9 + 7
    prefix_sum = [0]
    for num in nums:
        prefix_sum.append(prefix_sum[-1] + num)
    dp = [0] * (len(nums) + 1)
    dp[0] = 1
    for i in range(1, len(nums) + 1):
        for j in range(i):
            if prefix_sum[i] - prefix_sum[j] >= k:
                dp[i] = (dp[i] + dp[j]) % MOD
    return dp[-1]

