def minimumTotalCost(nums1: List[int], nums2: List[int]) -> int:
    n = len(nums1)
    cost = 0
    for i in range(n):
        if nums1[i] == nums2[i]:
            min_cost = float('inf')
            for j in range(n):
                if nums1[j] != nums2[i] and i != j:
                    min_cost = min(min_cost, i + j)
            if min_cost == float('inf'):
                return -1
            else:
                nums1[i], nums1[min_cost - i] = nums1[min_cost - i], nums1[i]
                cost += min_cost
    return cost

