def maxPoints(grid: List[List[int]], queries: List[int]) -> List[int]:
    m, n = len(grid), len(grid[0])
    points = [[0]*n for _ in range(m)]
    directions = [(0, 1), (0, -1), (1, 0), (-1, 0)]

    for i in range(m):
        for j in range(n):
            for dx, dy in directions:
                nx, ny = i + dx, j + dy
                if 0 <= nx < m and 0 <= ny < n:
                    points[i][j] = max(points[i][j], grid[nx][ny])

    result = []
    for query in queries:
        visited = [[False]*n for _ in range(m)]
        heap = [(-grid[0][0], 0, 0)]
        max_point = 0
        while heap:
            point, x, y = heapq.heappop(heap)
            point = -point
            if point > query:
                break
            if visited[x][y]:
                continue
            visited[x][y] = True
            max_point += 1
            for dx, dy in directions:
                nx, ny = x + dx, y + dy
                if 0 <= nx < m and 0 <= ny < n and not visited[nx][ny] and points[nx][ny] <= query:
                    heapq.heappush(heap, (-points[nx][ny], nx, ny))
        result.append(max_point)
    return result

