def magnificentSets(n: int, edges: List[List[int]]) -> int:
    graph = defaultdict(list)
    for u, v in edges:
        graph[u].append(v)
        graph[v].append(u)

    visited = [0] * (n + 1)
    group = [0] * (n + 1)

    def dfs(node, grp):
        visited[node] = 1
        group[node] = grp
        for neighbor in graph[node]:
            if visited[neighbor] == 0:
                if not dfs(neighbor, grp ^ 1):
                    return False
            elif group[neighbor] == group[node]:
                return False
        return True

    for i in range(1, n + 1):
        if visited[i] == 0 and not dfs(i, 0):
            return -1

    return max(group) + 1

